import React, { useState } from "react";
export function SearchPhotos() {

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    // console.log(SpeechRecognition)





    if(SpeechRecognition) {
        console.log("Your Browser supports speech Recognition");

        const recognition = new SpeechRecognition();
        recognition.continuous = true;
        // recognition.lang = "en-US";

        searchForm.insertAdjacentHTML("beforeend", '<button type="button"><i class="fas fa-microphone"></i></button>');
        searchFormInput.style.paddingRight = "50px";

        const micBtn = searchForm.querySelector("button");
        const micIcon = micBtn.firstElementChild;

        micBtn.addEventListener("click", micBtnClick);
        function micBtnClick() {
          if(micIcon.classList.contains("fa-microphone")) { // Start Voice Recognition
            recognition.start(); // First time you have to allow access to mic!
          }
          else {
            recognition.stop();
          }
        }

        recognition.addEventListener("start", startSpeechRecognition); // <=> recognition.onstart = function() {...}
        function startSpeechRecognition() {
          micIcon.classList.remove("fa-microphone");
          micIcon.classList.add("fa-microphone-slash");
          searchFormInput.focus();
          console.log("Voice activated, SPEAK");
        }

        recognition.addEventListener("end", endSpeechRecognition); // <=> recognition.onend = function() {...}
        function endSpeechRecognition() {
          micIcon.classList.remove("fa-microphone-slash");
          micIcon.classList.add("fa-microphone");
          searchFormInput.focus();
          console.log("Speech recognition service disconnected");
        }

        recognition.addEventListener("result", resultOfSpeechRecognition); // <=> recognition.onresult = function(event) {...} - Fires when you stop talking
        function resultOfSpeechRecognition(event) {
          const current = event.resultIndex;
          const transcript = event.results[current][0].transcript;

          if(transcript.toLowerCase().trim()==="stop recording") {
            recognition.stop();
          }
          else if(!searchFormInput.value) {
            searchFormInput.value = transcript;
          }
          else {
            if(transcript.toLowerCase().trim()==="go") {
              searchForm.submit();
            }
            else if(transcript.toLowerCase().trim()==="reset input") {
              searchFormInput.value = "";
            }
            else {
              searchFormInput.value = transcript;
            }
          }
          // searchFormInput.value = transcript;
          // searchFormInput.focus();
          // setTimeout(() => {
          //   searchForm.submit();
          // }, 500);
        }

        info.textContent = 'Voice Commands: "stop recording", "reset input", "go"';

    };











    const [query, setQuery] = useState("");
    const [labels, setlabels] = useState("");
    const [imageSelected, setImage] = useState(null);
    const [pics, setPics] = useState([]);

    const searchPhotos = async (e) => {
        e.preventDefault()
        console.log(`search for ${query}`);
        await fetch(`https://ri9wp1d2z2.execute-api.us-east-1.amazonaws.com/a2/search?q=${query}`,
                {headers:{
                    "x-api-key": 'jiVJQZYBAQa7DAi0PNYHj8lDrVHG5ADn1tYgS3Xu',
                }
                }
            )
            .then(response => response.json())
            .then(data => setPics(data));
    };

    const fileSelectHandeler = event => {
        setImage(event.target.files[0])
    }

    function uuidv4() {
        // ref:https://stackoverflow.com/questions/105034/how-to-create-a-guid-uuid
        return ([1e7]+-1e3+-4e3+-8e3+-1e11).replace(/[018]/g, c =>
          (c ^ crypto.getRandomValues(new Uint8Array(1))[0] & 15 >> c / 4).toString(16)
        );
    }

    const uploadPhotos = async (e) => {
        e.preventDefault()
        if (imageSelected) {
            console.log(`upload`);
            await fetch(`https://ri9wp1d2z2.execute-api.us-east-1.amazonaws.com/a2/b2a26998008/${uuidv4()}.${imageSelected.name.split('.').slice(-1)[0]}`,
                    {
                        method: "put",
                        headers:{
                        "Content-Type": `image/png`,
                        "x-amz-meta-customLabels": labels,
                        "x-api-key": 'jiVJQZYBAQa7DAi0PNYHj8lDrVHG5ADn1tYgS3Xu',
                        },
                        body: imageSelected
                    }
                )
        }
    };


    return (
        <>
        <form className="form" onSubmit={searchPhotos}> 
            <label className="label" htmlFor="query"> </label>
            <input
                type="text"
                name="query"
                className="input"
                placeholder={`Try "dog" or "apple"`}
                value={query}
                onChange={(e) => setQuery(e.target.value)}
            />
            <button type="submit" className="button">Search</button>
        </form>
        <button type="submit" className="voice-button">ðŸŽ¤</button>
        <br></br>

        <form className="form" onSubmit={uploadPhotos}>
            <input type="file" id="img" name="img" accept="image/png, image/jpg" onChange={fileSelectHandeler}/>
            <input
                type="text"
                name="labels"
                className="input"
                placeholder={`Labels`}
                value={labels}
                onChange={(e) => setlabels(e.target.value)}
            />
            <button type="submit" className="button">Upload</button>
        </form>

        <br></br>
        <br></br>
        <div className="card-list">
            {
                pics.map((pic) => 
                <div className="card" key={pic} >
                <img
                    className="card--image"
                    src={pic}
                    width="50%"
                    height="50%"
                ></img>
                </div>)};
        </div>
    </>
    );
}
